js:
var html = request(MY_PARAMS.link,{headers:{"User-Agent":PC_UA,"Cookie":fetch('hiker://files/cache/doubancookie.txt')}});
var sources = [];
var sources_list = html.match(/sources\[[1389]{1}\][\s\S]*?\]/g);
if (sources_list == null) {
    var script_list = parseDomForArray(html, 'body&&script[src]');
    for (var i in script_list) {
        var sources_url = parseDomForHtml(script_list[i], 'script&&src').indexOf('mixed_static') != -1 ? (parseDomForHtml(script_list[i], 'script&&src')) : sources_url
    }
    var sources_list = request(sources_url).match(/sources\[[1389]{1}\][\s\S]*?\]/g);
}
if (sources_list != null) {
    for (var i in sources_list) {
        eval(unescape(sources_list[i]));
    }
    var sources_title = ['腾讯视频', '优酷视频', '哔哩哔哩', '爱奇艺视频'];
    var sources_id = ['1', '3', '8', '9'];
    var latest_sources = new Array(1);
    for (var i in sources_title) {
        if (sources[sources_id[i]] != null) {
            if (sources[sources_id[i]].length >= latest_sources.length) {
                var latest_sources = sources[sources_id[i]];
                var latest_id = i;
            }
        }
    }
    var latest = sources_title[latest_id] + '\t\t更新至第' + latest_sources.length + '集';
} else {
    var latest = '暂无片源';
}
setResult(latest);

