js:
let myCollection_el = [],
    searchMode = getVar('myCollection-searchMode', ''),
    [_, pageNum, keyword] = MY_URL.split('##'),
    RULE = $.require('hiker://page/dataLoad?rule=' + getItem('myCollection')).filter((v) => v.search_url)
pageNum = parseInt(pageNum)
if (!searchMode && pageNum > RULE.length) setResult([])

let searchCount = { title: '⬆ 已搜索了' + pageNum + '个小程序 ⬆\n⬇ 下面还有' + (RULE.length - pageNum) + '个小程序 ⬇' }
RULE = searchMode ? RULE.find((v) => v.title == searchMode) : RULE[pageNum - 1]
// 处理MY_URL和MY_RULE
MY_RULE.version = RULE.version
MY_RULE.group = RULE.group
MY_RULE.title = RULE.title
MY_RULE.ua = RULE.ua
MY_RULE.col_type = RULE.col_type
MY_RULE.detail_col_type = RULE.sdetail_col_type
MY_RULE.find_rule = RULE.searchFind
MY_RULE.detail_find_rule = ['', '*'].includes(RULE.sdetail_find_rule) ? RULE.detail_find_rule : RULE.sdetail_find_rule
MY_RULE.preRule = RULE.preRule
MY_RULE.pageList = JSON.parse(RULE.pages || '[]')
MY_RULE.pages = JSON.stringify(MY_RULE.pageList)
MY_RULE.last_chapter_rule = RULE.last_chapter_rule
MY_RULE.params = {}

const { runCode, urlParse } = $.require('hiker://page/utility?rule=' + getItem('myCollection'))
var { MY_URL, indexHtml } = urlParse(RULE.search_url, searchMode ? pageNum : 1, (url) => {
    url[0] = url[0].replace(url[0].includes('%%') ? /%%/g : /\*\*/g, encodeStr(keyword, url[2]))
})
MY_RULE.url = MY_URL
// 正文解析
if (pageNum == 1) eval(MY_RULE.preRule)
if (MY_RULE.find_rule.startsWith('js:')) {
    function getUrl() {
        return MY_URL
    }

    function getResCode() {
        return indexHtml
    }

    function setResult(el, param1, param2, param3) {
        param1 = CALLBACK_KEY
        param2 = MY_RULE
        param3 = MY_TYPE
        if (Array.isArray(el.data)) el = el.data
        log(el)
        Array.prototype.push.apply(myCollection_el, el.map((v) => {
            let RULE = Object.assign({}, MY_RULE)

            if (!v.url) {
                return v
            } else if (v.url.includes('@rule=')) {
                let [_, url, rule] = v.url.match(/^([\s\S]*?)@rule=([\s\S]*)$/)
