        url: $("").lazyRule(() => {
            putVar("_label", "直链");
            refreshPage();
            return "toast://已切换" + getVar("_label");
        }),
        col_type: "text_2"
    });

} catch (e) {
    log(e.message)
}

//掏直链
if (/get_playsource/.test(MY_URL)) {
    eval(html);
    let indexList = QZOutputJson.PlaylistItem.indexList;
    for (let i in indexList) {
        let dataUrl = 'https://s.video.qq.com/get_playsource?id=' + sourceId + '&plat=2&type=4&data_type=3&range=' + indexList[i] + '&video_type=10&plname=qq&otype=json';
        eval(fetch(dataUrl, {}));
        // push.apply等同于python的extend
        video_lists.push.apply(video_lists, QZOutputJson.PlaylistItem.videoPlayList);
    }
