        return {
            title: it.title,
            pic_url: it.pic,
            desc: it.episode_number + '\t\t\t播放量：' + it.thirdLine,
            url: it.playUrl,
            col_type: 'movie_1',
        }
    });
    d = d.concat(data);
} else {
    let json = JSON.parse(html);
    video_lists = json.c.video_ids;
    let url = 'https://v.qq.com/x/cover/' + sourceId + '.html';
    if (json.c.type === 10) {
        // log('综艺');
        let dataUrl = 'https://s.video.qq.com/get_playsource?id=' + json.c.column_id + '&plat=2&type=2&data_type=3&video_type=8&plname=qq&otype=json';
        eval(fetch(dataUrl, {}));
        video_lists = [];
        let indexList = QZOutputJson.PlaylistItem.indexList;
        for (let i in indexList) {
            let dataUrl = 'https://s.video.qq.com/get_playsource?id=' + json.c.column_id + '&plat=2&type=4&data_type=3&range=' + indexList[i] + '&video_type=10&plname=qq&otype=json';
            eval(fetch(dataUrl, {}));
            video_lists.push.apply(video_lists, QZOutputJson.PlaylistItem.videoPlayList)
        }
        let data = video_lists.map((it) => {
            return {
                title: it.title,
                pic_url: it.pic,
                desc: it.episode_number + '\t\t\t播放量：' + it.thirdLine,
                url: $(it.playUrl).lazyRule(() => {
                    require('https://gitea.com/AI957/Hiker/raw/m/v/Route.js')
                    return aytmParse(input)
                }),
                col_type: 'movie_1',
            }
        });
        d = d.concat(data);
    } else if (video_lists.length === 1) {
        // log('电影或者电视剧只有1集');
        eval(JSON.parse(fetch("hiker://page/rel_link")).rule)
        var dc = $(url).lazyRule(() => {
            require('https://gitea.com/AI957/Hiker/raw/m/v/Route.js')
            return aytmParse(input)
        });
        d.push({
            title: '在线播放',
            img: 'https://s1.ax1x.com/2020/11/06/BhRK91.jpg',
            col_type: 'icon_2',
            url: _label == "直链" ? true_url : dc
        })
    } else if (video_lists.length > 1) {
        // log('电视剧');
        for (let i = 0; i < video_lists.length; i += 30) {
            video_list.push(video_lists.slice(i, i + 30))
        }
        for (let i in video_list) {
            let it = video_list[i];
            let o_url = 'https://union.video.qq.com/fcgi-bin/data?otype=json&tid=682&appid=20001238&appkey=6c03bbe9658448a4&union_platform=1&idlist=' + it.join(',');
            eval(fetch(o_url, {}));
            let col_type = QZOutputJson.results[0].fields.title.length > 12 ? 'movie_2' : 'movie_2';

            let dat = QZOutputJson.results.map((it) => {
