        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0)'
    }
})
const empty = "hiker://empty"

const categories = pdfa(html, 'body&&.nav-list').concat(pdfa(html, 'body&&.myui-screen__list'));

let init_cate = []

for (let i = 0; i < 20; i++) {
    init_cate.push("0")
}

const fold = getVar("555.fold", "0")
const cate_temp_json = getVar("555.category", JSON.stringify(init_cate))
const cate_temp = JSON.parse(cate_temp_json)


if (parseInt(page) === 1) {
    d.push({
        title: fold === '1' ? '““””<b><span style="color: #FF0000">∨</span></b>' : '““””<b><span style="color: #1aad19">∧</span></b>',
        url: $(empty).lazyRule((fold) => {
            putVar("555.fold", fold === '1' ? '0' : '1');
            refreshPage(false);
            return "hiker://empty"
        }, fold),
        col_type: 'scroll_button',
    })


    categories.forEach((category, index) => {
        let sub_categories = pdfa(category, 'body--a&&a:not(:contains(APP)):not(:contains(求片))');
        if (index === 0) {
            sub_categories.forEach((item, key) => {
                let title = pdfh(item, 'a&&Text')
                d.push({
                    title: key.toString() === cate_temp[index] ? '““””<b><span style="color: #098AC1">' + title + '</span></b>' : title,
                    url: $(pd(item, 'a&&href')).lazyRule((params) => {
                        let new_cate = []
                        params.cate_temp.forEach((cate, index) => {
                            new_cate.push(index === 0 ? params.key.toString() : "0")
                        })
                        putVar("555.category", JSON.stringify(new_cate))
                        putVar("555.url", input)
                        refreshPage(true)
                        return "hiker://empty"
                    }, {
                        cate_temp: cate_temp,
                        key: key,
                        page: page,
                    }),
                    col_type: 'scroll_button',
                })
            })
            d.push({
                col_type: "blank_block"
            });
        } else if (fold === '1') {
            sub_categories.forEach((item, key) => {
                let title = pdfh(item, 'a&&Text')
                d.push({
                    title: key.toString() === cate_temp[index] ? '““””<b><span style="color: #098AC1">' + title + '</span></b>' : title,
                    url: $(pd(item, 'a&&href')).lazyRule((params) => {
                        params.cate_temp[params.index] = params.key.toString()

                        putVar("555.category", JSON.stringify(params.cate_temp))
                        putVar("555.url", input)
                        refreshPage(true)
                        return "hiker://empty"
                    }, {
                        cate_temp: cate_temp,
                        index: index,
                        key: key,
                        page: page,
                    }),
                    col_type: 'scroll_button',
                })
            })
            d.push({
                col_type: "blank_block"
            });
        }
    })
}
