var lazy = $('').lazyRule(() => {
    var H = decodeURIComponent(base64Decode(JSON.parse(request(input).match(/player_.*?=(.*?)</)[1]).url));
    eval(getCryptoJS())

    function De(word) {
        var a = CryptoJS.enc.Utf8.parse(md5('rXjWvXl6'))
        var t = CryptoJS.enc.Utf8.parse('NXbHoWJbpsEOin8b')
        return CryptoJS.AES.decrypt(word, a, {
            iv: t,
            mode: CryptoJS.mode.CBC,
            padding: CryptoJS.pad.Pkcs7
        }).toString(CryptoJS.enc.Utf8)
    }
    if (H.indexOf('duoduo') != -1) {
        return De(pdfh(request('https://bo.movie06.com/ddplay/?url=' + H,{headers:{'Referer':MY_URL}}), 'script,-4&&Html').split("'")[1])
    } else if (H.indexOf('m3u8') != -1) {
        return H
    } else {
        var play = De(pdfh(request('https://jhpc.manduhu.com/?url=' + H), 'script,-1&&Html').split('"')[3]);
        if (/mgtv/.test(H)) {
            play += ';{User-Agent@Mozilla/5.0 (Windows NT 10.0)}'
        } else if (/bili/.test(H)) {
            play += ';{User-Agent@Mozilla/5.0&&Referer@https://www.bilibili.com/}'
        }
        return play + '#isVideo=true#'
    }
});
