@js:
var html=result,
header={"Referer":baseUrl};
(function getImgList(){
    var p = "picdata = \\[(.*?)\\];";
    var p1 = "imgDomain = \\'(.*?)\\';";
    var path = html.match(p)[1].replace(/\"/g, "").replace(/\\/g, "").split(",");
    var imgDomain = html.match(p1)[1];
    var piclist = new Array([path.length]);
    for (var i = 0; i < path.length; i++) {
        piclist[i] = imgDomain + path[i];
    }
    return piclist;
}()).map(uri=>
'<img src="'+uri+"," + JSON.stringify(header)+'">').join("\n")

