;function getImgList(){
    var chapterImages = html.match(/chapterImages = (\[[^\]]*?\])/)[1];
	var chapterPath = html.match(/chapterPath = "([^"]*?)"/)[1];
	return JSON.parse(chapterImages).map(function(image){return "https://res02.333dm.com/" +chapterPath+ image;});
}

function getImgList() {     
var str = html.match(/\'fs\':  \[(.*?)\]/);
var list = str[1].match(/sinmh[^\"]+/g);
var piclist = new Array([list.length]);        
for (var i = 0; i < list.length; i++) {   
piclist[i] = "http://ccimg1.61mh.com/" + list[i];        
}       
return piclist;    
}

