var jpgstr = result.match(jp)[1];
var mp = +result.match(p)[1];
var piclist = new Array(mp); 
for (var i = 0; i < mp; i++) {         
piclist[i] = jpgstr+(new Array(4).join('0') + (i+1)).slice(-4)+".jpg";
}
html='';
for(i in piclist){
html+='<img src="'+piclist[i]+'">'
}
