const domin_ = getVar('xjj_domin')
const lib_path_default_ = domin_ + '/file/lib/'

$.exports = (libName) => {
    var lib_cache = 'xjj_' + libName + '_lib'
    var lib_res = getVar(lib_cache)
    if (lib_res == '') {
        lib_res = request(lib_path_default_ + libName)
        putVar(lib_cache, lib_res)
    }
    return eval(lib_res)
}"}]

