let batchSearch_el = [],
  batchSearch_page = parseInt(MY_URL.slice(15)),
  batchSearch_kw = getVar('batchSearch-kw', ''),
  batchSearch_config = JSON.parse(getVar('batchSearch-config')),
  batchSearch_start = $.toString((mode, title) => {
    if (mode == '内置') {
      putVar('batchSearch-kw', input)
      refreshPage()
      return 'hiker://empty'
    } else return 'hiker://search?s=' + input + '&rule=' + title
  }, batchSearch_config.searchMode, MY_RULE.title)
eval(JSON.parse(fetch('hiker://page/reConfig')).rule)

if (batchSearch_page == 1) {
  batchSearch_el.push({
    title: '搜索',
    url: batchSearch_reConfig('if(input&&batchSearch_config.history.indexOf(input)==-1)batchSearch_config.history.push(input)') + ';' + batchSearch_start,
    extra: { defaultValue: batchSearch_kw },
    col_type: 'input'
  }, {
    title: '““””<span style="color:#409eff"><b>策略设置</b></span>',
    url: 'hiker://empty#策略设置',
    col_type: 'scroll_button'
  })
  Object.keys(batchSearch_config.filters).forEach((v) => batchSearch_el.push({
    title: (v == batchSearch_config.apply ? '““””<span style="color:#12b668">' : '') + v,
    url: v == batchSearch_config.apply ? 'hiker://empty' : '#noLoading#@lazyRule=.js:' +
      batchSearch_reConfig($.toString((apply) => { batchSearch_config.apply = apply }, v)),
    col_type: 'scroll_button'
  }))
  batchSearch_el.push({ col_type: 'line_blank' })
  if (!batchSearch_kw) {
    batchSearch_el.push({
      title: '““””<big><b>搜索历史</b></big> &nbsp 🗑',
      url: 'confirm://要清空搜索历史吗?.js:' + batchSearch_reConfig('batchSearch_config.history=[]'),
      col_type: 'text_1'
    })
    batchSearch_config.history.forEach((v) => {
      batchSearch_el.push({
        title: '““””<big>&nbsp &nbsp </big>' + v + '<big> &nbsp &nbsp</big>',
        url: v + '#noLoading#@lazyRule=.js:' + batchSearch_start,
        col_type: 'flex_button'
      })
    })
  }
}

if (batchSearch_kw) {
  eval(JSON.parse(fetch('hiker://page/searchLoad')).rule)
  Array.prototype.push.apply(batchSearch_el, batchSearch_load().map((v) => {
    if (v.content) v.desc += '\n' + v.content
    return v
  }))
}

setResult(batchSearch_el)
