detail_find_rule:
js:
let batchSearch_el = [],
  batchSearch_config = JSON.parse(getVar('batchSearch-config')),
  batchSearch_allGroup = JSON.parse(fetch('hiker://home')).map((v) => v.group).filter((v, i, arr) => arr.indexOf(v) == i),
  batchSearch_anotherMode = { '内置': '原生', '原生': '内置' } [batchSearch_config.searchMode]
eval(JSON.parse(fetch('hiker://page/reConfig')).rule)

batchSearch_el.push({
  title: '搜索线程: ' + batchSearch_config.threadCnt,
  url: 'input://////线程数: 同时搜索的小程序数量.js:' +
    batchSearch_reConfig('batchSearch_config.threadCnt=parseInt(input)'),
  img: 'hiker://images/设置'
}, {
  title: '超时(毫秒): ' + batchSearch_config.responseTime,
  url: 'input://////超时毫秒数: 搜索每个小程序时的最大访问时间.js:' +
    batchSearch_reConfig('batchSearch_config.responseTime=parseInt(input)'),
  img: 'hiker://images/历史'
}, {
  title: '搜索模式: ' + batchSearch_config.searchMode,
  url: 'confirm://要切换搜索模式为' + batchSearch_anotherMode + '吗?\n内置模式: 首页同屏显示搜索结果\n原生模式: 调用视界搜索显示结果.js:' +
    batchSearch_reConfig($.toString((mode) => { batchSearch_config.searchMode = mode }, batchSearch_anotherMode)),
  img: 'hiker://images/云备份'
}, {
  title: '新增策略',
  url: 'input://////给新策略起个名字吧.js:' +
    batchSearch_reConfig('batchSearch_config.filters[input]={group:[],exclude: []}'),
  img: 'hiker://images/书签'
}, {
  title: '““””<b>💾 &nbsp 应用并返回</b>',
  url: $('#noLoading#').lazyRule(() => (back(true), 'hiker://empty')),
  col_type: 'text_center_1'
})

for (let batchSearch_filtersName in batchSearch_config.filters) {
  let batchSearch_filtersGroup = batchSearch_config.filters[batchSearch_filtersName].group,
    batchSearch_filtersExclude = batchSearch_config.filters[batchSearch_filtersName].exclude
  batchSearch_el.push({ col_type: 'line_blank' }, {
    title: '““””<b>' + batchSearch_filtersName + '</b> &nbsp 🗑',
    url: 'confirm://要删除策略【' + batchSearch_filtersName + '】吗?.js:' +
      batchSearch_reConfig($.toString((name) => { delete batchSearch_config.filters[name] }, batchSearch_filtersName)),
    col_type: 'text_center_1'
  }, { col_type: 'big_blank_block' }, { col_type: 'big_blank_block' }, {
    title: '““””<span style="color:#409eff">[⊗]<b></b></span>',
    url: 'confirm://要清空策略【' + batchSearch_filtersName + '】包含的分组吗?.js:' +
      batchSearch_reConfig($.toString((name) => { batchSearch_config.filters[name].group = [] }, batchSearch_filtersName)),
    col_type: 'scroll_button'
  }, {
    title: '““””<span style="color:#409eff">[⊙]<b>分组:</b></span>',
    url: 'confirm://要全选策略【' + batchSearch_filtersName + '】包含的分组吗?.js:' +
      batchSearch_reConfig($.toString((name, allGroup) => { batchSearch_config.filters[name].group = allGroup }, batchSearch_filtersName, batchSearch_allGroup)),
    col_type: 'scroll_button'
  })
  let batchSearch_sortedGroup = batchSearch_allGroup.reduce((self, v) => {
    let i = batchSearch_filtersGroup.indexOf(v)
    if (i >= 0) self[0].push([i, v])
    else self[1].push(v)
    return self
  }, [
    [],
    []
  ])
  batchSearch_sortedGroup[0].forEach((v) => batchSearch_el.push({
    title: '““””<span style="color:#12b668">' + (v[1] || '[无分组]'),
    url: '#noLoading#@lazyRule=.js:' +
      batchSearch_reConfig($.toString((name, locat) => { batchSearch_config.filters[name].group.splice(locat, 1) }, batchSearch_filtersName, v[0])),
    col_type: 'scroll_button'
  }))
  batchSearch_sortedGroup[1].forEach((v) => batchSearch_el.push({
    title: v || '[无分组]',
    url: '#noLoading#@lazyRule=.js:' +
      batchSearch_reConfig($.toString((name, title) => { batchSearch_config.filters[name].group.push(title) }, batchSearch_filtersName, v)),
    col_type: 'scroll_button'
  }))
  batchSearch_el.push({ col_type: 'line' }, { col_type: 'big_blank_block' }, { col_type: 'big_blank_block' }, {
    title: '““””<span style="color:#409eff">[⊕]<b>排除:</b></span>',
    url: 'input://////要排除什么小程序?\n可输入多个，每行一个.js:' +
      batchSearch_reConfig($.toString((name) => { input.match(/^.*?$/gm).forEach((v) => batchSearch_config.filters[name].exclude.push(v)) }, batchSearch_filtersName)),
    col_type: 'scroll_button'
  })
  batchSearch_filtersExclude.forEach((v, i) => batchSearch_el.push({
    title: '““””<span style="color:#12b668">' + v,
    url: 'confirm://要取消排除小程序【' + v + '】吗?.js:' +
      batchSearch_reConfig($.toString((name, locat) => { batchSearch_config.filters[name].exclude.splice(locat, 1) }, batchSearch_filtersName, i)),
    col_type: 'scroll_button'
  }))
}

setResult(batchSearch_el)

