let batchSearch_path = 'hiker://files/rules/BatchSearchConfig.json',
  batchSearch_config = getVar('batchSearch-config') || fetch(batchSearch_path)
try { batchSearch_config = JSON.parse(batchSearch_config) } catch (e) { batchSearch_config = {} }

if (!(batchSearch_config.threadCnt > 0)) batchSearch_config.threadCnt = 6
if (!(batchSearch_config.responseTime > 0)) batchSearch_config.responseTime = 5000
if (['内置', '原生'].indexOf(batchSearch_config.searchMode) == -1) batchSearch_config.searchMode = '内置'
if (!Array.isArray(batchSearch_config.history)) batchSearch_config.history = []
if (!Object.keys(batchSearch_config.filters || {}).length) batchSearch_config.filters = {
  '全部': {
    group: JSON.parse(fetch('hiker://home')).map((v) => v.group).filter((v, i, arr) => arr.indexOf(v) == i),
    exclude: []
  }
}
if (!(batchSearch_config.apply in batchSearch_config.filters)) batchSearch_config.apply = Object.keys(batchSearch_config.filters)[0]

putVar('batchSearch-config', JSON.stringify(batchSearch_config))
writeFile(batchSearch_path, JSON.stringify(batchSearch_config))

