[{"name":"设置函数","path":"reConfig","rule":"function batchSearch_reConfig(code) {
  return $.toString((code) => {
    let batchSearch_config = JSON.parse(getVar('batchSearch-config'))
    new Function('batchSearch_config', code)(batchSearch_config)
    putVar('batchSearch-config', JSON.stringify(batchSearch_config))
    refreshPage(false)
    return 'hiker://empty'
  }, code)
}
"},{"col_type":"movie_1_vertical_pic","name":"搜索函数","path":"searchLoad","rule":"function batchSearch_load() {
  try {
    function batchSearch_runCode(batchSearch_rule) {
      if (batchSearch_rule.indexOf('.js:') >= 0) {
        try {
          var input = batchSearch_rule.split('.js:')[0]
          return eval(batchSearch_rule.split('.js:')[1])
        } catch (e) {
          return batchSearch_rule
        }
      } else {
        return batchSearch_rule
      }
    }
    // 搜索策略过滤
    let batchSearch_filtersGroup = batchSearch_config.filters[batchSearch_config.apply].group,
      batchSearch_filtersExclude = batchSearch_config.filters[batchSearch_config.apply].exclude,
      batchSearch_homeList = JSON.parse(fetch('hiker://home')).filter((item) =>
        item.searchFind && item.title != MY_RULE.title &&
        batchSearch_filtersGroup.find((v) => item.group == v) &&
        !batchSearch_filtersExclude.find((v) => item.title == v))
      .slice((batchSearch_page - 1) * batchSearch_config.threadCnt, batchSearch_page * batchSearch_config.threadCnt)
    if (!batchSearch_homeList.length) return []
    let batchSearch_res = [],
      batchSearch_ruleList = [],
      batchSearch_urlList = []
    batchSearch_homeList.forEach((item) => {
      try {
        // 缓存解析规则
        batchSearch_ruleList.push({
          title: item.title,
          searchFind: item.searchFind,
          sdetailFind: ['', '*'].indexOf(item.sdetail_find_rule) == -1 ? item.sdetail_find_rule : item.detail_find_rule,
          col_type: ['', '*'].indexOf(item.sdetail_find_rule) == -1 ? item.sdetail_col_type : item.detail_col_type,
          preRule: item.preRule,
          urlParam: (item.search_url.match(/;.*/) || [''])[0],
          ua: item.ua
        })
        // 搜索链接处理
        let searchUrl = item.search_url.split(';').map((v) => v.replace(/；；/g, ';'))
        searchUrl[0] = searchUrl[0].replace(searchUrl[0].indexOf('%%') >= 0 ? /%%/g : /\*\*/g, encodeStr(batchSearch_kw, searchUrl[2]))
          .replace(/fypage(?:@(-?\d+)@)?(?:\*\d+@)?/g, (_, $1) => parseInt($1 || 0) + 1)
        let searchReq = { url: batchSearch_runCode(searchUrl[0]), options: { headers: { 'Content-Type': 'text/plain; charst=UTF-8' }, method: searchUrl[1], timeout: batchSearch_config.responseTime } }
        // post方法时转换参数
        if (/^post$/i.test(searchReq.options['method'])) {
          let [oriUrl, body] = searchReq.url.split('?')
          searchReq.url = oriUrl.replace('？？', '?')
          if (/^JsonBody=/.test(body)) body = body.slice(9)
          searchReq.options['body'] = body
        }
        if (searchUrl[2]) searchReq.options.headers['Content-Type'] = searchReq.options.headers['Content-Type'].slice(0, 19) + searchUrl[2]
        if (searchUrl[3]) searchUrl[3].match(/{(.*)}/)[1].split('&&').forEach((v) => {
          let headers = v.split('@')
          searchReq.options.headers[headers[0]] = batchSearch_runCode(headers[1])
        })
        // 添加全局UA
        if (!searchReq.options.headers['User-Agent']) {
          if (item.ua == 'pc')
            searchReq.options.headers['User-Agent'] = PC_UA
          else if (item.ua == 'mobile')
            searchReq.options.headers['User-Agent'] = MOBILE_UA
        }
        batchSearch_urlList.push(searchReq)
      } catch (e) {
        batchSearch_urlList.push({ url: 'hiker://error/' + JSON.stringify(e) })
      }
    })
    // 获取搜索结果
    batchFetch(batchSearch_urlList).forEach((batchSearch_searchHtml, batchSearch_searchIndex) => {
      let batchSearch_itemRule = batchSearch_ruleList[batchSearch_searchIndex],
        batchSearch_itemUrl = batchSearch_urlList[batchSearch_searchIndex]
      MY_URL = batchSearch_itemUrl.url
      try {
        if (!batchSearch_searchHtml) {
          if (batchSearch_itemUrl.url.indexOf('hiker://error/') == 0)
            throw { message: '链接处理时出错:\n' + JSON.parse(batchSearch_itemUrl.url.slice(14)).message }
          else if (batchSearch_itemUrl.url.indexOf('hiker://') != 0) throw { message: '无法访问，未获取到网页源代码' }
        }

        function batchSearch_urlHandle(url, addRule) {
          // 子页面添加rule
          if (url.indexOf('hiker://page/') == 0) {
            [, url, params, mark] = /^(.*?)(?:\?(.*?))?(#.*?)?$/.exec(url)
            return url + '?rule=' + batchSearch_itemRule.title + (params || '') + (mark ? '&' + mark : '')
          }
          // 添加二级规则
          if (addRule && !/@(?:r|lazyR)ule=/.test(url)) {
            if (batchSearch_itemRule.sdetailFind) {
              if (!/;get|;post/i.test(url))
                url += ';get;' + batchSearch_itemUrl.options.headers['Content-Type'].split('charst=')[1] +
                ';{' + Object.keys(batchSearch_itemUrl.options.headers)
                .filter((v) => v != 'Content-Type')
                .map((v) => v + '@' + batchSearch_itemUrl.options.headers[v].replace(/;/g, '；；'))
                .join('&&') + '}'
              batchSearch_itemRule.urlParam.replace(/^;post/i, ';get')
              url += '@rule=' + (batchSearch_itemRule.sdetailFind.indexOf('js:') == 0 ?
                batchSearch_itemRule.sdetailFind :
                batchSearch_itemRule.sdetailFind.replace('==>', ';' + batchSearch_itemRule.col_type + '==>')
              )
            } else url = 'web://' + url
          }
          // 二级页面预处理与错误处理
          if (url.indexOf('@rule=js:') > 0) url = url.replace('@rule=js:',
            '@rule=js:try{\nbatchSearch_itemRule=' + $.stringify({ title: batchSearch_itemRule.title, col_type: batchSearch_itemRule.col_type }) +
            ';' + $.stringify(batchSearch_urlHandle) + ';' + $.stringify(batchSearch_reConfig) + ';' +
            $.toString(() => {
              fetch = (function(func) {
                return function(url, params) {
                  return func(batchSearch_urlHandle(url), params)
                }
              })(fetch)
              setResult = (function(func) {
                return function(el) {
                  if (!Array.isArray(el)) el = el.data
                  func(el.map((v) => {
                    v.url = v.url && batchSearch_urlHandle(v.url)
                    v.col_type = v.col_type || batchSearch_itemRule.col_type
                    return v
                  }))
                }
              })(setResult)
              setSearchResult = setResult
              setHomeResult = setResult
            }) + ';') + '\n}catch(e){' + $.toString((title, batchSearch_reConfig) => {
            setResult([{
              title: '小程序【' + title + '】中，此二级页面不支持或者已失效，错误信息：\n\n' + e.message,
              col_type: 'long_text'
            }, { col_type: 'line' }, {
              title: '““””<span style="color:#409eff">排除小程序</span>',
              url: 'confirm://要排除该小程序吗?.js:' +
                batchSearch_reConfig('batchSearch_config.filters[batchSearch_config.apply].exclude.push("' + title + '")').replace('refreshPage(false)', '') + ';' +
                $.toString(() => writeFile('hiker://files/BatchSearchConfig.json', getVar('batchSearch-config'))) +
                ';"toast://已排除小程序【' + title + '】"',
              col_type: 'text_center_1'
            }])
          }, batchSearch_itemRule.title, batchSearch_reConfig) + '}'
          return url
        }
        if (batchSearch_itemRule.searchFind.indexOf('js:') == 0) {
          // js解析
          Array.prototype.push.apply(batchSearch_res, function() {
            let batchSearch_result = []

            function getUrl() { return MY_URL }
            fetch = (function(func) {
              return function(url, params) {
                return func(batchSearch_urlHandle(url), params)
              }
            })(fetch)
            eval(batchSearch_itemRule.preRule)

            function getResCode() { return batchSearch_searchHtml }

            function setResult(el) {
              if (!Array.isArray(el)) el = el.data
              el.forEach((v) => {
                v.title = (batchSearch_config.searchMode == '内置' && !/^““|‘‘/.test(v.title) ? '““””' : '') + v.title
                v.url = batchSearch_urlHandle(v.url, true)
                v.desc = batchSearch_itemRule.title + (v.desc ? ' 🔹 ' + v.desc : '')
                v.col_type = 'movie_1_vertical_pic'
                batchSearch_result.push(v)
              })
            }
            setSearchResult = setResult
            setHomeResult = setResult
            eval(batchSearch_itemRule.searchFind.slice(3))
            return batchSearch_result
          }())
        } else {
          // 普通解析
          let find = batchSearch_itemRule.searchFind.split(';')
          parseDomForArray(batchSearch_searchHtml, find[0]).forEach((data) => {
            let [title, url, desc, content, img] = find.slice(1).map((v, i) =>
                batchSearch_runCode(v == '*' ? '' :
                  (i == 1 || i == 4 ? parseDom(data, v) :
                    parseDomForHtml(data, v)))),
              item = {
                title: (batchSearch_config.searchMode == '内置' && !/^““|‘‘/.test(title) ? '““””' : '') + title,
                url: batchSearch_urlHandle(url, true),
                desc: batchSearch_itemRule.title + (desc ? ' 🔹 ' + desc : ''),
                content: content,
                img: img,
                col_type: 'movie_1_vertical_pic'
              }
            if (item.title.indexOf(batchSearch_kw) >= 0 || item.desc.indexOf(batchSearch_kw) >= 0) batchSearch_res.push(item)
          })
        }
      } catch (e) {
        // 搜索结果错误处理
        batchSearch_res.push({
          title: '小程序【' + batchSearch_itemRule.title + '】不支持或者已失效，点击查看错误信息',
          url: 'confirm://' + e.message + '\n\n是否排除该小程序?.js:' +
            batchSearch_reConfig('batchSearch_config.filters[batchSearch_config.apply].exclude.push("' + batchSearch_itemRule.title + '")').replace('refreshPage(false)', '') + ';' +
            $.toString(() => writeFile('hiker://files/BatchSearchConfig.json', getVar('batchSearch-config'))) +
            ';"toast://已排除小程序【' + batchSearch_itemRule.title + '】"',
          col_type: 'text_center_1'
        });
      }
    })
    return batchSearch_res
  } catch (e) {
    return [{
      title: '搜索时出现致命错误，点击查看解决办法\n' + e,
      url: $('点击确定后结束搜索，将搜索线程调整为1，\n重新搜索直到再次出现相同的报错，\n然后就可以定位引起致命错误的小程序，手动将其排除').confirm(() => {
        putVar('batchSearch-kw', '')
        refreshPage()
      }),
      col_type: 'text_center_1'
    }]
  }
}
"}]

