let todoRule = JSON.parse(getVar('batchEdit-todoRule', '[]')),
  editAttr = JSON.parse(getVar('batchEdit-editAttr', '["group"]')),
  subMode = getVar('batchEdit-subMode', '字串'),
  replaceMode = getVar('batchEdit-replaceMode', '字串'),
  subStr = getVar('batchEdit-subStr', ''),
  replacement = getVar('batchEdit-replacement', ''),
  quickCopy = ['/^.*$/', '/[\\s\\S]*/', 'new RegExp("","")', '()=>{return ""}', 'function(){return ""}', 'new Function("", "")'],
  el = []

el.push({
  title: '““””<b>恢复备份',
  url: $('#noLoading#').lazyRule(() => {
    if (!fetch('hiker://files/share/batchEdit-backup-rules.json')) return 'toast://备份为空'
    else return 'rule://' + base64Encode('海阔视界￥home_rule_url￥hiker://files/share/batchEdit-backup-rules.json')
  })
}, {
  title: '““””<b>使用须知',
  url: 'hiker://page/manual#noHistory##noRecordHistory#',
}, {
  title: '““””<b>编辑队列: ' + todoRule.length,
