js:
var res = {};
var items = [];

// 所有开关  开则 true  关则 false
// 规则去重开关
let clearDuplicate = true;

var keyList = [];
// TODO 优化能匹配 hiker://xxx 的链接
var domainRegExp = new RegExp("([a-zA-Z0-9][-a-zA-Z0-9]{0,62}(\\.[a-zA-Z0-9][-a-zA-Z0-9]{0,62})+\\.?)");
function isKeyInList(key) {
    if (keyList.indexOf(key) > -1) {
        return true;
    }
    return false;
}

let rules = JSON.parse(getResCode());
// let url = getUrl();
let url = "hiker://home";
/hiker:\/\/(.*)/.test(url);
let category = RegExp.$1;
switch (category) {
    case "home":
        for (let i = 0; i < rules.length; i++) {
            let rule = rules[i];
            let key = domainRegExp.test(rule.url) === true ? RegExp.$1: "";
            // TODO 修复匹配出 fypage.html 的问题
            if (key === "" || key === "fypage.html" || isKeyInList(key) === true) {
                continue;
            }
            keyList.push(key);
        }
        break;
    case "bookmark":
        break;
    default:
        setError("未知分类，无法解析");
        break;
}

var mRulesList = new Array(keyList.length);
for (let i = 0; i < mRulesList.length; i++) {
    let rulesTemp = rules;
    mRulesList[i] = rulesTemp.filter(item => {
        let result = false;
        try {
            result = item.url.indexOf(keyList[i]) > -1;
        } catch (e) {  result = false;  }
        return result;
    });
}

var keyNum = 0;
var itemsTemp = [];
for (let i = 0; i < mRulesList.length; i++) {
    let mRules = mRulesList[i];
    if (mRules.length >= 2) {
        itemsTemp.push({
            title: keyList[i],
            col_type: "text_1"
        });
        keyNum++;
        for (let j = 0; j < mRules.length; j++) {
            let rule = mRules[j];
            itemsTemp.push({
                title: rule.title,
                url: "hiker://home@" + rule.title,
            });
        }
    }
}

if (clearDuplicate === true){
    var obj = {};
    itemsTemp = itemsTemp.reduce((current, next) => {
        obj[next.title] ? "" : obj[next.title] = true && current.push(next);
        return current;
    }, []);
}

items.push({
    title: "总规则数：" + rules.length + "，当前待处理：" + keyNum + " 组",
    desc: "少掉的规则去哪里了？当然是不重复的规则啦！\n(然而还有一部分是匹配失败的结果🙈️...)",
    col_type: "pic_1"
});

while (itemsTemp.length) items.push(itemsTemp.shift());

res.data = items;
setHomeResult(res);

