const matchList  = {
  // "<":"&lt;",
  // ">": "&gt;",
  // "&": "&amp;",
  // '"': "&quot;",
  // "'": "acute;",
  " ": "&nbsp;",
  "\n":"<br>"
}
// 字符过滤器
function htmlEscape(text){
  let regStr = '(' + Object.keys(matchList).toString() + ')'
  regStr = regStr.replace(/,/g, ')|(')  
  const regExp = new RegExp(regStr, 'g')
  return text.replace(regExp, match => matchList[match])
}
