d.push({
    title:'📥导入TXT',
    col_type:'scroll_button',
    url:$().rule(()=>{
        let d = [];
        d.push({
            title:'导入说明',
            col_type:'text_1',
            desc:'支持此插件导出格式的文本进行导入。\n格式1: 名称,地址 一行一个解析\n格式2:云剪切板 魔断解析导入口令¥¥剪切板地址',
            url:'hiker://empty'
        });
        d.push({
            desc: "txt文本解析规则,一行一个",
            col_type: 'input',
            title:'解析文本',
            extra: {
                titleVisible: false,
                defaultValue: getVar('dn.import_text', ''),
                type: "textarea",
                height:-1,
                onChange: 'putVar("dn.import_text",input)'
            }
        });
        d.push({
            title:'📥导入',
            col_type:'text_center_1',
            url:$('确定编辑好了吗?此操作将覆盖本地已有的记录，建议备份后用空文件导入').confirm(()=>{
                const {defaultSets} = $.require("hiker://page/demoData?rule=MyFieldᴰⁿ");
                let parseRoute = 'hiker://files/rules/DuanNian/MyParse.json';
                let jsCode = fetch(parseRoute)||JSON.stringify({title:[],codes:{},settings:defaultSets});
                try {
                    eval('var json =' + jsCode);
                }
                catch (e) {
                    return 'toast://发生错误:\n'+e.message
                }
                let toImport = getVar("dn.import_text");
                if(/魔断解析导入口令¥¥/.test(toImport)&&toImport.split('¥¥').length>1){
                    let url = toImport.split('¥¥')[1];
                    if(!/^http/.test(url)){
                        return 'toast://剪切板地址有误,导入失败'
                    }
                    toImport = parsePaste(url);
                    toImport = base64Decode(toImport);
                }
                let jiexi = toImport.split('\n').filter(it=>it.trim());
                let parseTitle = [];
                let parseCode = {};
                for (let i in jiexi) {
                    let title=jiexi[i].split(',')[0];
                    let code=jiexi[i].split(',')[1];
                    parseTitle.push(title);
                    //log(title);
                    if(!(code.replace(/[\s]/g,'').startsWith('http'))){
                        // log(title+'为函数解析');
                        if(code.replace(/[\s]/g,'').length>20){//视为function
                            try {
                                code = eval(base64Decode(code));
                                // log(code);
                            }catch (e) {
                                code = e.message;
                            }
                        }
                    }
                    parseCode[title]=code;
                }
                let scObject = ["CityIP", "pcUA", "cacheM3u8", "defaultParseWeb","maoss", "LLQ"];
                parseTitle = parseTitle?parseTitle.filter(it=>!scObject.includes(it)):[];
                var newParseTitle = json.title.concat(parseTitle);
                json.title = Array.from(new Set(newParseTitle));
                Object.assign(json.codes, parseCode);
                let newFile = $.stringify(json); //JSON.parse会报错
                writeFile(parseRoute, newFile);
                refreshPage(false);
                return 'toast://已成功导入'+jiexi.length+'条解析';
            }),
            extra:{
                lineVisible:true
            }
        });
        setResult(d);
    }),
});
d.push({
    title:'☯️查重处理',
    col_type:'scroll_button',
    url:$().lazyRule((unique2,duplicates,floors)=>{
        let parseRoute = 'hiker://files/rules/DuanNian/MyParse.json';
        let jsCode = fetch(parseRoute);
        try {
            eval('var json =' + jsCode);
        }
        catch (e) {
            return 'toast://本地那个MyParse.json文件有问题,这个功能不给你用了!'
        }
        let titles = json.title;
        let codes = json.codes;
        let keys= Object.keys(codes);
        let keys_re_ct = keys.length - unique2(keys).length; // 名称重复
        let normal_list = [];
        for(let o in codes){
            if(typeof(codes[o])!=='undefined'&&typeof(codes[o])!=='function'){
                normal_list.push(codes[o]);
            }
        }
        let reNorList = unique2(normal_list);
        let value_re_ct = normal_list.length - reNorList.length; //代码重复
        let values_re = duplicates(normal_list);//重复的值
        let ftlist = [];
        let flags = {};
        let myFloors = floors;
        for(let j in codes){//遍历所有
            let one = codes[j];
            try {
                if (typeof(one) !== 'function'&&values_re.includes(one)) {
                    if (typeof (flags[one]) === 'undefined' || !flags[one]) {
                        //取出第一个标志
                        flags[one] = myFloors[0];
                        myFloors.splice(0, 1);//删除标志列表里的第一个元素
                    }
                    ftlist.push({key: flags[one] + j, value: one});
                } else {
                    ftlist.push({key: j, value: one});
                }
            }catch (e) {
                log(e.message)
            }
        }
        // log(JSON.stringify(flags));
        let newCodes = {};
        for(let k in ftlist){
            newCodes[ftlist[k].key] = ftlist[k].value;
        }
        let tips = '检测到名称复用项目数:'+keys_re_ct+',代码重复项目数量:'+value_re_ct+'\n需要一键打个标识吗?不支持自动删除,打标志自行解决';
        return $(tips).confirm((value_re_ct,json,newCodes,parseRoute)=>{
            if(value_re_ct<1){
                return 'toast://既然都是0个重复项目了，就不需要我做什么了!'
            }
            function compare(){
                return function (a, b){
                    const {floors} = $.require("hiker://page/demoData?rule=MyFieldᴰⁿ");
                    let x = a.substring(0,1);
                    let y = b.substring(0,1);
                    return floors.indexOf(y)-floors.indexOf(x);
                }
            }
            json.codes = newCodes;
            let titles = Object.keys(newCodes);
            titles = titles.sort(compare());
            json.title = titles;
            var newFile = $.stringify(json); //JSON.parse会报错
            writeFile(parseRoute, newFile);
            refreshPage(false);
            return 'toast://已一键打上标志';
        },value_re_ct,json,newCodes,parseRoute)
    },unique2,duplicates,floors),
});
