        func = function(url, title, index) {
            return $(url, "请输入「" + title + "」的新接口").input((title, index) => {
                let parseUrlText = request("hiker://files/jiexi/jiexi.txt");
                let parseUrlArray = parseUrlText.split(/\n/);
                let selected1 = parseUrlArray[index];
                parseUrlArray[index] = title + "&&" + input;
                parseUrlText = parseUrlArray.join("\n");
                writeFile("hiker://files/jiexi/jiexi.txt", parseUrlText);
                if (selected1 == getItem("selected0")) {
                    setItem("selected", input);
                    setItem("selected0", title + "&&" + input);
                }
                refreshPage(false);
                return "toast://修改成功";
            }, title, index);
        };
        break;
    case "2":
        func = function(url, title, index) {
            return $("确认删除「" + title + "」？").confirm((title, index) => {
                let parseUrlText = request("hiker://files/jiexi/jiexi.txt");
                let parseUrlArray = parseUrlText.split(/\n/);
                let selected1 = parseUrlArray[index];
                parseUrlArray.splice(index, 1);
                parseUrlText = parseUrlArray.join("\n");
                writeFile("hiker://files/jiexi/jiexi.txt", parseUrlText);
                if (selected1 == getItem("selected0")) {
                    if (parseUrlArray[0]) {
