//随机从列表抽取一个元素
function getOne(its) {return  its[Math.floor(Math.random()*its.length)];}
//去重（ES6 Set）
function unique2(array){
    return Array.from(new Set(array));
}
//获取列表中重复都值
function duplicates(arr) {
    let res=arr.filter((x,k)=>{
        let a=k*1+1;
        let index=arr.indexOf(x,a);
        if(index>-1){
            return x;
        }
    })
    let re=new Set(res);
    return Array.from(re);
}
$.exports.getOne = getOne;
$.exports.unique2 = unique2;
