                putVar(this.key, newValue);
            }

        },
        keyEncodeType: {
            key: "crypto_key_encode_type",
            get: function () {
                // TODO 此处不知为何 Crypto.ConfigMapping.keyEncodeType["UTF-8"] 会报 Crypto undefined
