                putVar(this.key, newValue);
            }
        },
        ivEncodeType: {
            key: "crypto_iv_encode_type",
            get: function () {
                return getVar(this.key, "UTF-8")
            },
            set: function (newValue) {
                putVar(this.key, newValue);
            }
        },
    },
    onClose: function() {
        let configKeys = Object.keys(this.config);
        for(let confKey of configKeys) {
            let key = this.config[confKey].key;
            clearVar(key);
        }
    },
    buildConfig: function() {
        let mode = this.config.mode.get();
        let pad = this.config.pad.get();
        let iv = this.config.iv.get();
        let conf = {};
        conf.mode = this.ConfigMapping.mode[mode];
        conf.padding = this.ConfigMapping.pad[pad]
        if (mode != "ECB" && iv.length != 0) {
            conf.iv = CryptoJS.enc.Utf8.parse(iv);
            if (this.config.ivEncodeType.get() == "Hex") {
                iv = iv.replace("[HEX]:", "")
                conf.iv = CryptoJS.enc.Hex.parse(iv);
