        }
        return conf
    },
    encryptor: function(obj) {
        // 原文
        let text = obj.decText;
        if (text) {
            let keyString = this.config.key.get()
            let key = CryptoJS.enc.Utf8.parse(keyString);
            if (this.config.keyEncodeType.get() == "Hex") {
                keyString = keyString.replace("[HEX]:", "")
                key = CryptoJS.enc.Hex.parse(keyString);
            }
            try {
                let conf = this.buildConfig()
                let encrypted = this.ConfigMapping.encType[this.config.type.get()](text, key, conf);
                let encode = this.config.encode.get()
                let cipherText = encrypted.toString();
                if (encode === 'HEX') {
                    cipherText = CryptoJS.enc.Hex.stringify(CryptoJS.enc.Base64.parse(cipherText));
                }
                // 密文
                this.config.cipherText.set(cipherText)
                return cipherText;
            } catch (e) {
                log(e.message)
                setError(e.message)
            }
        } else {
            throw new Error("明文不能为空！")
        }
    },
    decryptor: function(obj) {
        let text = obj.encText;
        if (text) {
            let encode = this.config.encode.get()
            let cipherText;
            if (encode === 'Base64') {
                cipherText = text
            } else if (encode === 'HEX') {
                cipherText = CryptoJS.enc.Base64.stringify(CryptoJS.enc.Hex.parse(text))
            }
            let keyString = this.config.key.get()
            let key = CryptoJS.enc.Utf8.parse(keyString);
            if (this.config.keyEncodeType.get() == "Hex") {
                keyString = keyString.replace("[HEX]:", "")
                key = CryptoJS.enc.Hex.parse(keyString);
            }
            try {
                let conf = this.buildConfig()
                let decrypted = this.ConfigMapping.decType[this.config.type.get()](cipherText, key, conf);
                let decryptedStr = decrypted.toString(CryptoJS.enc.Utf8);
                if (!decryptedStr) {
                    throw new Error("请检查密钥 key、偏移量 iv 以及密文是否正确！");
                }
                this.config.text.set(decryptedStr)
                return decryptedStr
            } catch (e) {
                throw new Error("解密失败！" + e.message);
            }
        } else {
            throw new Error("密文不能为空！")
        }
    },
    checkKey: function (obj) {
        let key = Crypto.config.key.get();
        if (key.length === 0) { 
            throw new Error("密钥 key 不能为空！")
        }
        let type = Crypto.config.type.get();
        let blockSize = 128;
        try {
            blockSize = Crypto.ConfigMapping["blockSize"][type][Crypto.config.blockSize.get()]
            // log("checkKey#blockSize:" + blockSize)
        } catch (e) { }
        if (type === "DES") {
            blockSize = 64;
        }
        if (Crypto.config.keyEncodeType.get() == "Hex") {
            key = key.replace("[HEX]:", "")
            blockSize = blockSize * 2;
        }
        // 设置数据块长度
        let keySize = key.length * 8;
        // log("checkKey#keySize:" + keySize)
        // log("checkKey#keySize/8:" + keySize/8)
        // log("checkKey#blockSize:" + blockSize)
        // log("checkKey#blockSize/8:" + blockSize / 8)
        if (keySize < blockSize) {
            obj.from = Crypto.config.keyEncodeType.key
            let newKey = Crypto.fillKeys(obj, key, blockSize / 8);
            Crypto.config.key.set(newKey)
            // log("checkKey#newKey: " + newKey)
            // log("checkKey#newKey.Size: " + newKey.length * 8)
            throw new Error('密钥 key 长度过短，已自动 `\\x00` 空填充，请再次执行加/解密')
        } else if (keySize > blockSize) {
            // TODO 超出部分将被忽略
            throw new Error("密钥 key 长度 " + key.length + " 过长，长度应为 " + blockSize / 8 +"，请自行检查，然后重新执行加/解密")
        } else {
            return;
        }
    },
    checkIv: function (obj) {
        let iv = Crypto.config.iv.get();
        let mode = Crypto.config.mode.get();
        if (mode === 'ECB') {
            return;
        }
        if (iv.length === 0) {
            throw new Error("偏移量 iv 不能为空！")
        }
        let type = Crypto.config.type.get();
        let blockSize = 128;
        if (type === 'DES') {
            blockSize = 64;
        }
        if (Crypto.config.ivEncodeType.get() == "Hex") {
            iv = iv.replace("[HEX]:", "")
            blockSize = blockSize * 2;
        }
        // 设置数据块长度
        let ivSize = iv.length * 8;
        if (ivSize < blockSize) {
            obj.from = Crypto.config.ivEncodeType.key
            let newIv = Crypto.fillKeys(obj, iv, blockSize / 8);
            Crypto.config.iv.set(newIv)
            throw new Error("偏移量 iv 长度过短，已自动 `\\x00` 空填充，请再次执行加/解密")
        } else if (ivSize > blockSize) {
            throw new Error("偏移量 iv 长度 " + iv.length + " 过长，长度应为 " + blockSize / 8 + "，请自行检查，然后重新执行加/解密")
        } else {
            return;
        }
    },
    /**
     * 密钥 `\x00` 空填充
     * @param {string} key 密钥
     * @param {Number} keySize 填充长度, 值: 128, 192, 256
     */
    fillKeys: function (obj, key, keySize) {
        let Tool = $.require("hiker://page/utiliy?rule=" + obj.ruleTitle);
        let encodeType = "UTF-8"
        if (obj.from == Crypto.config.keyEncodeType.key) {
            encodeType = Crypto.config.keyEncodeType.get();
        } else if (obj.from == Crypto.config.ivEncodeType.key) {
            encodeType = Crypto.config.ivEncodeType.get();
        }
        if (encodeType == "Hex") {
            return "[HEX]:" + Tool.fillZeroPadding(key, keySize);
        }
        return Tool.fillNullPadding(key, keySize);
    },
    AES: {
        encrypt: function(obj) {
            Crypto.config.type.set("AES")
            // log("AES-Encrypt-obj:" + JSON.stringify(obj))
            return Crypto.encryptor(obj);
        },
        decrypt: function(obj) {
            Crypto.config.type.set("AES")
            return Crypto.decryptor(obj);
        }

    },
    DES: {
        encrypt: function(obj) {
            Crypto.config.type.set("DES")
            return Crypto.encryptor(obj);
        },
        decrypt: function(obj) {
            Crypto.config.type.set("DES")
            return Crypto.decryptor(obj);
        }

    },
}

