let Tool = {
  randomString: function(len){
      let chars = 'ABCDEFGHJKMNPQRSTWXYZabcdefhijkmnprstwxyz123456789' 
      let maxLen = chars.length
      let str = ''
      for (let i = 0; i < len; i++) {
        str += chars.charAt(Math.floor(Math.random() * maxLen))
      }
      return str
  },
  fillNullPadding: function (str, len) {
    // str.padStart(targetLength [, padString])
    // str.padEnd(targetLength [, padString])
    if(str.length >= len) {
      return str;
    }
    return str + Array( len - str.length + 1 ).join("\x00");
  },
  fillZeroPadding: function (str, len) {
    // str.padStart(targetLength [, padString])
    // str.padEnd(targetLength [, padString])
    if(str.length >= len) {
      return str;
    }
    return str + Array( len - str.length + 1 ).join("0");
  },
}
$.exports = Tool;"},{"col_type":"text_1","name":"散列哈希类页面","path":"hash","rule":"js:
