js:
const d = [];
const programUrl = 'hiker://files/rules/Allstar/programlist.json'
const weekStr = ['日', '一', '二', '三', '四', '五', '六'];

let programListjson = JSON.parse(fetch(programUrl));
let programList = programListjson.data;
let toastMsg = programListjson.toastMsg;
let toastUrl = programListjson.toastUrl;

const zeroPadding = (num) => ('00' + num).slice(-2);

// 取yyyy-mm-dd
const getYMD = (dtval) => {
  let dt = new Date(dtval);
  let y = dt.getFullYear();
  let m = zeroPadding(dt.getMonth() + 1);
  let d = zeroPadding(dt.getDate());
  return (y + '-' + m + '-' + d);
}

// 取yyyy-mm-dd + 1
const getTomorrowYMD = (dt) => getYMD(dt.setDate(dt.getDate() + 1));

// 生成放映时间信息(今日/今晚/明日/明晚/m.d(w)) hh:mm
const formatStreamingText = (programDateStr) => {
  let todayYMD = getYMD(new Date());
  let tomorrowYMD = getTomorrowYMD(new Date());
  let dataYMD = getYMD(programDateStr);
  let dataDate = new Date(programDateStr);

  let dateStr;
  let dataHours = dataDate.getHours();
  let dataMinutes = dataDate.getMinutes();

  if (todayYMD === dataYMD) {
    if (dataHours > 17)
      dateStr = '今晚';
    else
      dateStr = '今日';
  } else if (tomorrowYMD === dataYMD) {
    if (dataHours > 17)
      dateStr = '明晚';
    else
      dateStr = '明日';
  } else {
    dateStr = zeroPadding(dataDate.getMonth() + 1) + '.' + zeroPadding(dataDate.getDate()) + '(' + weekStr[dataDate.getDay()] + ')';
  }

  return dateStr + '<font color="#ff8000"> ' + zeroPadding(dataHours) + ':' + zeroPadding(dataMinutes) + ' </font>开播';
};

// 按时间排序
programList.sort((a, b) => new Date(a.start).getTime() - new Date(b.start).getTime());

programList.forEach((programObj, idx) => {
  let todayYMD = getYMD(new Date());
  let dataYMD = getYMD(programObj.start);

  // 跳过历史记录
  if (new Date(dataYMD).getTime() - new Date(todayYMD).getTime() < 0)
    return;

  d.push({
    title: '‘‘’’<strong>' + programObj.title + '</strong>\n' + formatStreamingText(programObj.start) + '\n主播: ' + programObj.streamer,
    desc: '‘‘’’' + programObj.desc,
    pic_url: programObj.pic,
    url: toastUrl,
    col_type: d.length === 0 ? 'movie_1_vertical_pic_blur' : 'movie_1_vertical_pic'
  });

});

if (d.length === 0) {
  d.push({
    title: '‘‘’’<strong>暂无内容</strong>',
    col_type: 'text_center_1'
  });
