// 读取远程文件并缓存
const reloadProgram = () => {
  let programListjson = fetch(remoteProgramPath);
  if (programListjson) {
    writeFile(localProgramPath, programListjson);
    writeFile(programCacheTimePath, new Date() + '')
  }
}

if (!fetch(programCacheTimePath) || !fetch(localProgramPath)) {
  reloadProgram();
}

let cacheDate = new Date(fetch(programCacheTimePath));
let nowDate = new Date();
let timeDiff = nowDate.getTime() - cacheDate.getTime();
if (timeDiff > cacheInterval * 60 * 60 * 1000) {
  reloadProgram();
}

