const Config = $.require("hiker://page/Config.json");

function task(taskParam) {
    let result = $.require("hiker://page/Batch.exe.js")(taskParam);
    return result;
}

$.exports = function(chapterTeam, info, MY_ID) {
    if (chapterTeam.length === 0) {
        return "toast://空的下载队列";
    }
    var count = 0;
    var taskCount = chapterTeam.length;
    var successCID = [];
    var failName = [];
    var logCount = 1;
    var taskTeam = chapterTeam.map(chapterItem => ({
        func: task,
        id: chapterItem.title,
        param: {
            chapterItem: chapterItem,
            info: info,
            MY_ID: MY_ID
        }
    }));
    let logNum = Config.logNum;

    function putLog(text) {
        log(text);
        if (logCount >= logNum) {
            deleteItem(MY_ID + "#log#" + (logCount - 10));
        }
        addItemAfter(MY_ID + "||日志", {
            url: "hiker://empty",
            title: "$>" + text,
            col_type: "text_1",
            extra: {
                lineVisible: false,
                id: MY_ID + "#log#" + logCount
            }
        });
        logCount++;
    }

    function listener(perch, id, error, CID) {
        count++;
        let isSuccess = false;
        if (error !== null) {
            failName.push(id);
            putLog("[listener]下载失败：" + id + "<ERROR@ " + error + ">");
        } else if (CID >= 0) {
            successCID.push(CID);
            let ratio = (count / taskCount).toFixed(4);
            let stripCount = Math.ceil(ratio * 10);
            isSuccess = true;
            updateItem({
                title: "下载进度：" + successCID.length + "/" + taskCount,
                desc: "““””" + "▇".repeat(stripCount).fontcolor("#ffac2d") + "▇".repeat(10 - stripCount).fontcolor("#e5e1e4") + " " + (ratio * 100) + "%",
                extra: {
                    id: MY_ID + "||下载进度"
                }
            });
            putLog("[listener]已完成下载：" + id);
        } else if (CID === -1) {
            failName.push(id);
            putLog("[listener]已停止：" + id);
        }
        if (taskCount !== count) return;
        if (successCID.length === taskCount) {
            updateItem({
                desc: "““””" + "▇".repeat(10).fontcolor("#00ff00") + " 100%",
                extra: {
                    id: MY_ID + "||下载进度"
                }
            });
        }
        let batchTeam = JSON.parse(getMyVar(MY_ID + ":batchTeam", "[]") || "[]");
        let status = getMyVar(MY_ID + ":status", "0");
        let failTeam = batchTeam.filter(v => !successCID.includes(v));
        putMyVar(MY_ID + ":batchTeam", JSON.stringify(failTeam));
        if (failTeam.length > 0 && status !== "1") {
            putMyVar(MY_ID + ":status", "3");
            putLog("下载失败:[" + failName.join("]，[") + "]。");
            updateItem({
                title: "[处理失败]",
                extra: {
                    id: MY_ID + "||下载控制"
                }
            });
        } else if (failTeam.length > 0 && status === "1") {
            putMyVar(MY_ID + ":status", "4");
            putLog("[listener]#已停止所有任务，但已有进度不会消失#");
            updateItem({
                title: "[重新开始]",
                extra: {
                    id: MY_ID + "||下载控制"
                }
            });
        } else {
            putMyVar(MY_ID + ":status", "2");
            putLog("下载任务[" + info.bookName + "]已全部成功完成");
            updateItem({
                title: "[返回]",
                extra: {
                    id: MY_ID + "||下载控制"
                }
            });
        }
        toast("下载任务[" + info.bookName + "]已完成");
    }
    let taskTeamChunk = [];
    if (Config.threadNum > 0) {
        taskTeamChunk = Tool.chunk(taskTeam, Config.threadNum);
    } else {
        taskTeamChunk = [taskTeam];
    }
    for (let taskList of taskTeamChunk) {
        be(taskList, {
            func: listener
        });
        let status = getMyVar(MY_ID + ":status", "0");
        if (status === "1") {
            let batchTeam = JSON.parse(getMyVar(MY_ID + ":batchTeam", "[]") || "[]");
            let status = getMyVar(MY_ID + ":status", "0");
            let failTeam = batchTeam.filter(v => !successCID.includes(v));
            putMyVar(MY_ID + ":batchTeam", JSON.stringify(failTeam));
            putMyVar(MY_ID + ":status", "4");
            putLog("[listener]#已停止所有任务，但已有进度不会消失#");
            updateItem({
                title: "[重新开始]",
                extra: {
                    id: MY_ID + "||下载控制"
                }
            });
            return "hiker://empty";
        }

    }

    return "hiker://empty";
};"},{"col_type":"movie_3","name":"#fg.批量主程序","path":"Batch.exe.js","rule":"const File = $.require("hiker://page/File.js");
