function txt(paths, isReplace) {
    showLoading("正在合并...");
    const File = $.require("hiker://page/File.js");
    const Tool = $.require("hiker://page/Tool.js");
    const Config = $.require("hiker://page/Config.json");

    let filelist = File.getFilePath(paths.path, "file", ".txt");
    let listLength = filelist.length;
    if (listLength === 0) {
        hideLoading();
        return "toast://空的章节";
    }
    let bufilelist = [];
    for (let i = 0; i < listLength; i++) {
        let file = filelist[i];
        let fileInfo = Tool.split(file.name, "$", 2);
        bufilelist.push({
            name: fileInfo[2].slice(0, fileInfo[2].lastIndexOf(".")),
            path: "file://" + file.path,
            id: Number(fileInfo[0]) || 0
        });
    }
    bufilelist.sort((a, b) => a.id - b.id);
    let entirePath = paths.path.replace(Config.novelPath, Config.novelFullPath) + ".txt";
    let entirePathto = isReplace ? entirePath + ".tmp" : entirePath;

    if (fileExist("file://" + entirePath)) {
        toast("初始化文件");
        if (!File.deleteFlies(entirePath)) {
            hideLoading();
            return "toast://初始化失败";
        } else {
            toast("初始化成功");
        }
    }

    for (let i = 0; i < listLength; i++) {
        let item = bufilelist[i];
        let content = request(item.path);
        File.fileWriteAppend(entirePathto, item.name + "\n　　" + content.replace(/^\s+|\s+$/gm, "").split(/\n+/).join("\r　　") + "\n\n");
    }
    if (isReplace) {
        let ruleList = JSON.parse(readFile("replaceRule.json") || "[]");
        ruleList = ruleList.filter(item => item.isEnabled);
        let len = ruleList.length;
        let replacePattern = ["replace", "replaceAll"];
        let flag = File.fileRule(entirePathto, entirePath, (javaString) => {
            for (let i = 0; i < len; i++) {
                let replaceRule = ruleList[i];
                let replace = replacePattern[0 + replaceRule.isRegex];
                javaString = javaString[replace](replaceRule.pattern, replaceRule.replacement);
            }
            return javaString.getBytes();
        });
        if (!flag) {
            hideLoading();
            return "toast://合并失败";
        }

    }
    hideLoading();
    return "toast://合并完成";
}
