    clearMyVar(".editReplaceRule.pattern");
    clearMyVar(".editReplaceRule.replacement");
    clearMyVar(".editReplaceRule.first");
    //clearMyVar(".editReplaceRule.scope");
}));
let ruleList = JSON.parse(readFile("replaceRule.json") || "[]");
let id = "#" + new Date().getTime();
//let replaceRule = isNew == "true"?{}:ruleList[index];
if (isNew == "true") {
    replaceRule = {
        id: new Date().getTime(),
        isEnabled: true,
        isRegex: false,
    }
} else {
    replaceRule = ruleList[index];
}
//首次初始化
if(getMyVar(".editReplaceRule.first","")==""){
    putMyVar(".editReplaceRule.isRegex", "" + replaceRule.isRegex);
    putMyVar(".editReplaceRule.first","1");
}
