    url: $("#noLoading#").lazyRule(() => {
        let att = ["name", "isRegex", "pattern", "replacement"];
        let json = {};
        att.forEach(key => {
            let value = getMyVar(".editReplaceRule." + key, "");
            if (key === "isRegex") {
                value = value === "true" ? true : false;
            }
            json[key] = value;
        });
        copy(JSON.stringify(json));
        return "hiker://empty";
    }),
    col_type: "text_2"
});
layout.push({
    title: "粘贴",
    url: $("{{clipboard}}").input(() => {
        try {
            let json = JSON.parse(input);
            if (Array.isArray(json) || json.pattern == undefined) return;
            let att = ["name", "isRegex", "pattern", "replacement"];
            att.forEach(key => {
                putMyVar(".editReplaceRule." + key, String(json[key]));
            });
            refreshPage(false);
        } catch (e) {
            log(e.toString());
        }
    }),
    col_type: "text_2"
});
layout.push({
    col_type: "input",
