"},{"col_type":"movie_3","name":"公用函数","path":"functions","rule":"function typeConvert(value, type) {
    let vType = Object.prototype.toString.call(value).slice(8, -1)
    switch (type) {
        case 'Object':
            switch (vType) {
                case 'Object':
                    return value
                case 'Array':
                    return value.reduce((obj, v, i) => {
                        obj[i] = value[i]
                        return obj
                    }, {})
                case 'String':
                    try {
                        return JSON.parse(value)
                    } catch (e) { return {} };
                default:
                    return {}
            };
        case 'Array':
            switch (vType) {
                case 'Object':
                    return Object.keys(value).map((k) => value[k])
                case 'Array':
                    return value
                case 'String':
                    try {
                        return JSON.parse(value)
                    } catch (e) { return [] };
                default:
                    return []
            };
        case 'String':
            switch (vType) {
                case 'Object':
                case 'Array':
                    return JSON.stringify(value)
                default:
                    return String(value)
            };
        case 'Number':
            return Number(value) || 0
        case 'Boolean':
            try { value = eval(value) } catch (e) {}
            return Boolean(value)
        case 'Null':
            return null
    }
}

function JSONRecur(json, path, value, isCode) {
    if (path.length == 1) {
        if (value === undefined) {
            return json[path[0]]
        } else if (typeof value == 'function') {
            json[path[0]] = value(json[path[0]])
        } else json[path[0]] = value
    } else {
        return JSONRecur(json[path[0]], path.slice(1), value, isCode)
    }
}
