       let devInfo="";
       try{
          devInfo=marked.parse(getMyVar('devInfo'));
       }catch(e){}
        setResult([
            {
                title:devInfo,
                col_type:'rich_text',
            }
        ]);
    })
});
d.push({
    desc: "快来编辑你的公告吧~",
    col_type: 'input',
    extra: {
        titleVisible: false,
        defaultValue: getMyVar('devInfo', '')||"",
        type: "textarea",
        height:-1,
        onChange: 'putMyVar("devInfo",input)'
    }
});
d.push({
    title:"保存公告",
    col_type: 'text_center_1',
    url:$('确认保存公告吗？将会与道长仓库服务器通讯').confirm(()=>{
        let dev_set_info = getMyVar("dev_set_info").split('$$');
        let input_text = getMyVar('devInfo', '');
        if(input_text.length >= 4){
            let info_set_url= dev_set_info[2];
            let send_data = {"params":
                    {"data": {"info": input_text},
                        "name": dev_set_info[0],
                        "password": dev_set_info[1]}
            };
            // log(send_data);
            let par2 = {headers:{'content-type':'application/json;charset=UTF-8','User-Agent':dev_set_info[3]},body:JSON.stringify(send_data),method:'POST'};
            // par2 = JSON.stringify(par2); //x5里面请求才需要编码
            try {
                let back_ret = request(info_set_url, par2);
                back_ret = JSON.parse(back_ret).result;
                if(back_ret.status===0){
                    // refreshPage();
                    return 'toast://保存成功\n'+back_ret.detail;
                }else{
                    return 'toast://保存失败\n'+back_ret.detail;
                }
            }catch (e) {
                return 'toast://错误\n'+e.message;
            }
        }else{
            confirm({title:'道长提示',content:'虽然你很懒，但是公告至少写4个字吧！'});
            return 'hiker://empty'
        }
    })
});

setResult(d);"}]
