[{"col_type":"movie_3","name":"一级公共模板","path":"yiji","rule":"/**
 * 建议复制粘贴到一个 js 文件，然后上传仓库或者码云，之后都作为依赖进行 requireCache 调用
 * 有能力者可修改一下自定义项再作为依赖 require
 */
function 一级() {};
Object.assign(一级.prototype, {
    constructor: 一级,
    test() {},
    获取源码(url, header) {
        return fetch(true_url, {
            headers: header || {
                'User-Agent': 'Mozilla/5.0 (Windows NT 10.0)'
            }
        }) // 默认统一使用PC UA
    },
    获取正确链接(page, regex) {
        let true_url = getMyVar('header.url', MY_URL);
        let 链接处理工具 = requireCache(config.链接处理工具依赖)
        true_url = 链接处理工具
            .链接(true_url)
            .页码(page)
            .插入新处理规则(regex || [])
            .获取处理结果();
        return true_url;
    },
    打造分类(ruleList) {
        if (!ruleList) {
            let 分类定位 = [
                // 定位四大金刚
                {
                    一级分类: 大类定位,
                    子分类: 统一小类定位 || 小类定位,
                    分类链接: {
                        二次处理(url) {
                            try {
                                return 对分类链接进行二次处理(url)
                            } catch (e) {
                                return url
                            }
                        }
                    }
                }
            ]

            if (拼接分类的大类定位 && (统一小类定位 || 拼接分类的小类定位)) {
                分类定位.push(
                    // 定位其他大分类，如地区、年份等
                    {
                        一级分类: 拼接分类的大类定位,
                        子分类: 统一小类定位 || 拼接分类的小类定位,
                        分类链接: {
                            二次处理(url) {
                                try {
                                    return 对拼接分类链接进行二次处理(url)
                                } catch (e) {
                                    return url
                                }
                            }
                        }
                    }
                )
            }
            ruleList = 分类定位;
        }
        // '0' 为默认不折叠，'1' 为默认折叠
        const 当前折叠状态 = getMyVar('header.fold', '1')

        // 引入动态分类依赖
        // 框架已经稳定，使用 requireCache 更佳
        let htmlCategories = requireCache(config.动态分类依赖)
        htmlCategories.界面(d)
            // .分类链接(true_url)
            .源码(html)
            .页码(page)
            .添加分类定位(ruleList)
            .开启内置折叠功能() // 必须
            .折叠(当前折叠状态) // 必须
            .选中的分类颜色(分类颜色)
            .开始打造分类();
    },
    生成片单(d, html) {
        let list = parseDomForArray(html, 列表);
        for (let j in list) {
            d.push({
                title: parseDomForHtml(list[j], 标题),
                desc: parseDomForHtml(list[j], 描述),
                img: parseDom(list[j], 图片) + '@Referer=',
                url: parseDom(list[j], 链接).replace('play', 'detail').replace('/sid/1/nid/1', '') + "#immersiveTheme#"
            });
        }
    }
})

$.exports = new 一级();
$.exports"}]

