        //fba.log(fba.getUrls());
        var urls = _getUrls();
        if (window.count == null || window.count == undefined) {
            window.count = 0
        }
        if (window.count >= 23) {
            return srcurl
        }
        window.count = window.count + 1;
        if (window.count > 1 && window.count <= 3 && window.count != null && window.count != undefined) {
            if (document.querySelector('body').innerText.search(/触发了防盗链|未授权|接口防盗|请到主站观看/) != -1) {
                if (!srcurl) {
                	fba.log('尝试跳防盗验证一');
                    location.href = location.href;
                } else {
                	fba.log('尝试跳防盗验证二');
                    location.href = srcurl
                }
            };
        } else if(window.count > 3 && window.count != null && window.count != undefined){
            if (urls.length < 1) {
                fba.hideLoading();
                return 'toast://检索失败，可能链接无法访问。';
            } else if (urls.length == 1) {
                fba.log('尝试直链与JSON解析');
                if (urls[0].match(/dycdn\-tos\.pstatp|\.m3u8|\.mp4|\.flv|netease\.com|video_mp4|type\=m3u8|pt\=m3u8/) && !urls[0].match(/\.html|m3u8\.tv|\&next|ac\=dm|\=http|https\:\/\/[\d]\.m3u8|\?url\=\/m3u8/)) {
                    //fy_bridge_app.log(urls[0])
                    if (urls[0].indexOf('bilivideo') != -1) {
                        return urls[0] + ';{Referer@https://www.bilibili.com&&User-Agent@Mozilla/5.0}';
                    } else if (urls[0].indexOf('titan.mgtv.com') != -1) {
                        return urls[0] + '#isVideo=true#' + ';{Referer@www.mgtv.com&&User-Agent@Mozilla/5.0}';
                    } else {
                        return urls[0]
                    };
                } else if (location.href.match(/dycdn\-tos\.pstatp|\.m3u8|\.mp4|\.flv|netease\.com|video_mp4|type\=m3u8|pt\=m3u8/) && !location.href.match(/html|m3u8\.tv|\&next|ac\=dm|\=http|https\:\/\/[\d]\.m3u8|\?url\=\/m3u8/)) {
                    return location.href;
