                    var html = fba.fetch(location.href, {});
                    if (!/\<meta/.test(html)&&!/\<html/.test(html)) {
                    return JSON.parse(html).url;
                    }else{
                    return location.href}
                };
            } else {
                fba.log('日志检索第' + window.count + '次');
                for (var i in urls) {
                    if (urls[i].match(/dycdn\-tos\.pstatp|\.m3u8|\.mp4|\.flv|netease\.com|video_mp4|type\=m3u8|pt\=m3u8/) && !urls[i].match(/html|m3u8\.tv|\&next|ac\=dm|\=http|https\:\/\/[\d]\.m3u8|\?url\=\/m3u8/)) {
                        //fy_bridge_app.log(urls[i])
                        if (urls[i].indexOf('bilivideo') != -1) {
