            title: getMyVar(taburl, '0') == i ? getHead(tabname) : tabname,
            col_type: 'scroll_button',
            url: $("#noLoading#").lazyRule((tabname, taburl, i) => {
                putMyVar('当前线路名', tabname);
                putMyVar(taburl, i);
                refreshPage();
                return 'hiker://empty'
            }, tabname, taburl, i)
        })
    }
}

function setLists(lists, index) {
    var list = lists[index];
    // log('选集有：'+list.length+' · 线路有：'+tabs.length)
    // 将反序归正
    try {
        if (pdfh(list[0], "a&&Text").match(/(\d+)/)[0] > pdfh(list.slice(-1)[0], "a&&Text").match(/(\d+)/)[0]) list.reverse()
    } catch (e) {}
    if (tabs.length > 0 || list.length > 2) {
        var icon_s = 'http://82.156.222.77/weisyr/icon/';
        if (getMyVar('选集排序') == 1) {
            var avatar = icon_s + '正序.svg'
        } else {
            var avatar = icon_s + '反序.svg'
        }

        d.push({
            col_type: 'line_blank'
        });

        d.push({
                title: '<font color="#778899">当前线路</font></small>' + getMyVar('当前线路名', tabs[0]) + "<small><font color='grey'>" + '\t\t共 ' + list.length + ' 条' +
                    "</font></small>",
                url: `@lazyRule=.js:if(getMyVar('选集排序')==1){putMyVar('选集排序', 0);}else{putMyVar('选集排序', 1)};refreshPage();'hiker://empty'`,
                col_type: 'avatar',
                img: avatar
            })


        d.push({
            col_type: 'line'
        });
    }

    for (let i = 0; i < 5; i++) {
        d.push({
            col_type: "blank_block"
        })
    }

     function playLists() {
        var jm = parseDomForHtml(list[j], 'a&&Text').replace(/第|集|话|期|-/g, '').replace(/预告/g, '📢');
		 if (parseDomForHtml(list[j], 'a&&Text').indexOf('展开') == -1) {
			 if (list.length < 5) {
				 var col = 'text_2'
			 } else {
				 var col = jm.length > 5 ? 'text_3' : 'text_5'
			 }
			 d.push({
				 title: jm,
				 url: parseDom(list[j], 'a&&href') + lazy,
				 extra: {
					 id: parseDom(list[j], 'a&&href'),
					 blockRules: ['.m4a', '.mp3', '.mp4', '.m3u8', '.flv', '.avi', '.3gp', '.mpeg', '.wmv', '.mov', '.rmvb', '.gif', '.jpg', '.jpeg', '.png', '.ico', '.svg']
				 },
				 col_type: col
			 });
		 }

    }
    // 开始选集分区
    addListener('onClose', $.toString(() => {
            clearMyVar('当前线路名');
            clearMyVar('分集起');
            clearMyVar('分集终');
        }))
        //选集＞则启用选集分区（list替换成你的选集数组）
    var 选集数组 = list; //改
    if (选集数组.length > 110) {
        //设置每区选集数目
        var page_number = 100;
        let 底部换页 = '关闭';
        var star = getMyVar('分集起', '1');
        var end = getMyVar('分集终', JSON.stringify(page_number));
        var total = Math.ceil(选集数组.length / page_number);
        var catalogue = []
        for (let i = 0; i < total; i++) {
            catalogue += i * page_number + ',';
            catalogue = catalogue.split(',');
        }
        for (let i = 0; i < 8; i++) {
            d.push({
                col_type: "blank_block"
            })
        }
        for (var i = 0; i < catalogue.length - 1; i++) {
            var total1 = parseInt(catalogue[i]) + 1;
            var total2 = parseInt(catalogue[i + 1]);
            if (i == (catalogue.length - 2)) var total2 = 选集数组.length;
            d.push({
                title: star == total1 ? '‘‘' + total1 + '-' + total2 + '’’' : total1 + '-' + total2,
                url: $("#noLoading#").lazyRule((total1, total2) => {
                    putMyVar('分集起', total1);
                    putMyVar('分集终', total2);
                    refreshPage(false);
                    return 'hiker://empty'
                }, total1, total2),
                col_type: 'scroll_button'
            });
        }
        if (getMyVar('选集排序') == 1) {
            for (var j = end - 1; j >= star - 1; j--) {
                // 打印选集列表
                playLists() //改
            }
        } else {
            for (var j = star - 1; j < end; j++) {
                // 打印选集列表
                playLists() //改
            }
        }
        //底部页码
        d.push({
            col_type: "line"
        })
        var yema = Math.ceil(end / page_number);
        d.push({
            title: "““”” <small><small><font color='grey'>" + yema + ' / ' + total + "</font> </small></small>",
            col_type: "text_center_1",
            extra: {
                lineVisible: false
            }
        })
        if (底部换页 == '开启') {
            d.push({
                title: '首页',
                url: $("#noLoading#").lazyRule((page_number) => {
                    putMyVar('分集起', '1');
                    putMyVar('分集终', page_number);
                    refreshPage(false);
                    return 'hiker://empty'
                }, page_number),
                col_type: 'text_4'
            });
            d.push({
                title: '上一页',
                url: $("#noLoading#").lazyRule((star, end, page_number, total, 选集数组) => {
                    var s = parseInt(star) - page_number;
                    var e = parseInt(end) - page_number;
                    if (end = 选集数组.length) var e = s + page_number - 1;
                    if (s < 1) {
                        var s = 1;
                        var e = page_number;
                    }
                    if (s > 0) {
                        putMyVar('分集起', s);
                        putMyVar('分集终', e);
                        log(s + '-' + e)
                        refreshPage(false);
                    }
                    return 'hiker://empty'
                }, star, end, page_number, total, 选集数组),
                col_type: 'text_4'
            });
            d.push({
                title: '下一页',
                url: $("#noLoading#").lazyRule((star, end, page_number, total, 选集数组) => {
                    var s = parseInt(star) + page_number;
                    var e = parseInt(end) + page_number;
                    log(s + '-' + e)
                    if (e > 选集数组.length) {
                        var s = (total - 1) * page_number + 1;
                        var e = 选集数组.length;
                    }
                    putMyVar('分集起', s);
                    putMyVar('分集终', e);
                    refreshPage(false);
                    return 'hiker://empty'
                }, star, end, page_number, total, 选集数组),
                col_type: 'text_4'
            });
            d.push({
                title: '尾页',
                url: $("#noLoading#").lazyRule((page_number, total, 选集数组) => {
                    var s = (total - 1) * page_number + 1;
                    putMyVar('分集起', s);
                    putMyVar('分集终', 选集数组.length);
                    refreshPage(false);
                    return 'hiker://empty'
                }, page_number, total, 选集数组),
                col_type: 'text_4'
            });
        }
    }

    // 结束选集分区
    else {
        if (getMyVar('选集排序') == 1) {
            for (var j = list.length - 1; j >= 0; j--) {
                playLists()
            }
        } else {
            for (var j = 0; j < list.length; j++) {
                playLists()
            }
        }
    }
}
