                        return JSON.parse(html).url;
                    } else {
                        fba.log(location.href)
                    }
                };
            } else {
                fba.log('网页加载日志检索' + window.count + '');
                for (var i in urls) {
                    if (urls[i].match(/miued\.com\/m3|dycdn\-tos\.pstatp|\.m3u8|\.mp4|\.flv|netease\.com|video_mp4|type\=m3u8/) && !urls[i].match(/html|m3u8\.tv|\&next|ac\=dm|\=http|https\:\/\/[\d]\.m3u8|\?url\=\/m3u8/)) {
