                            return urls[i] + '#isVideo=true#' + ';{Referer@www.mgtv.com&&User-Agent@Mozilla/5.0}';
                        } else if (urls[i].indexOf('juhaokan') != -1) {
                            return urls[i] + ';{Referer@https://www.juhaokan.cc/}';
                        } else if (urls[i].indexOf('ojbk') != -1) {
