var dlist = html.data;

var puh = [];
for (var i = 0; i < dlist.length; i++) {
    var list = dlist[i].list;
    var tytit = dlist[i].title
    for (var j = 0; j < list.length; j++) {
        var link = list[j].url;

        if (/APP影视/.test(tytit)) {
            if (link.indexOf('api.php/app/') != -1) {
                var hmurl = link + 'search?pg=fypage&text=' + key + '&token=';
            } else {
                var hmurl = link + '?page=fypage&limit=10&wd=' + key;
            }
        } else if (/TV影视/.test(tytit)) {
            if(/293ys|zhaikanys|ji\-ding\-he|phoebe|yunbo\.bxin|lanrentk/.test(link)){            
            var hmurl = link + '?ac=list&zm=' + key + '&page=fypage';                    
            }else{               
            var hmurl = link + '?ac=list&wd=' + key + '&page=fypage'; 
            }
        }

        puh.push({
            title: list[j].title,
            url: hmurl.replace(/fypage/g, '1'),
            link: list[j].url,
            type: tytit
        });
    } //for j
} //for i


//setError(puh);
var Data = [];
var Tit = [];
for (var j = le - ssxc; j < le; j++) {
    if (j < puh.length) {
        var arrt = puh[j].title;
        var Url = puh[j].url;
        var ptyp = puh[j].type;
        var tlink = puh[j].link;
        Data.push({
            url: Url,
            options: {
                headers: {
                    "User-Agent": MOBILE_UA
                },
                timeout: timeou
            }
        });
        Tit.push({
            tit: arrt,
            typnm: ptyp,
            link: tlink
        });
