                } else {
                    var url = 'hiker://empty$$' + litit + '$$' + turl + '$$/vodPhbAll$$';
                }
            }
            items.push({
                title: title,
                url: url,
                pic_url: img,
                col_type: 'icon_round_small_4'
            });
        }
    }
    setResult({
        data: items
    });
}

