let d = [];
//自定义颜色
  const Color="#40E0D0";

  const a=MY_URL.replace(/%%/g, "");
  let true_url = getVar("jh.url", a)
  const page=MY_URL.match(/%%(.*)%%/)[1];

//setError(page);

//翻页 需要根据实际替换
  true_url = true_url.replace('---/', +page+'---/')  
  const html = fetch(true_url)
  const empty = "hiker://empty"
//获取列表 
  const categories = parseDomForArray(html, '#main&&.scroll-content:not(:contains(全部类型))')

//setError(true_url);

  let init_cate = []

  for (let i = 0; i < 20; i ++) {
    init_cate.push("0")
  }

  // 取缓存
  const fold = getVar("jh.fold", "0")
  const cate_temp_json = getVar("jh.category", JSON.stringify(init_cate))
  const cate_temp = JSON.parse(cate_temp_json)


  if (parseInt(page) === 1) {
    d.push({
        title: fold === '1' ? '““””<b><span style="color: #FF0000">∨</span></b>' : '““””<b><span style="color: #1aad19">∧</span></b>',
        url: $(empty).lazyRule((fold) => {
            putVar("jh.fold", fold === '1' ? '0' : '1');
            refreshPage(false);
            return "hiker://empty"
        }, fold),
        col_type: 'scroll_button',
    })


    categories.forEach((category, index) => {
        let sub_categories = parseDomForArray(category, 'div&&a');
        if (index === 0) {
            sub_categories.forEach((item, key) => {
                let title = parseDomForHtml(item, 'a&&Text').replace('片库','')
                d.push({
                    title: key.toString() === cate_temp[index] ? '““””<b><span style="color:' + Color + '">' + title + '</span></b>' : title,
                    url: $(parseDom(item, 'a&&href')).lazyRule((params) => {
                        let new_cate = []
                        params.cate_temp.forEach((cate, index) => {
                            new_cate.push(index === 0 ? params.key.toString() : "0")
                        })
                        putVar("jh.category", JSON.stringify(new_cate))
                        putVar("jh.url", input)
                        refreshPage(true)
                        return "hiker://empty"
                    }, {
                        cate_temp: cate_temp,
                        key: key,
                        page: page,
                    }),
                    col_type: 'scroll_button',
                })
            })
            d.push({
                col_type: "blank_block"
            });
        } else if (fold === '1') {
            sub_categories.forEach((item, key) => {
                let title = parseDomForHtml(item, 'a&&Text')
                d.push({
                    title: key.toString() === cate_temp[index] ? '““””<b><span style="color:' + Color + '">' + title + '</span></b>' : title,
                    url: $(parseDom(item, 'a&&href')).lazyRule((params) => {
                        params.cate_temp[params.index] = params.key.toString()

                        putVar("jh.category", JSON.stringify(params.cate_temp))
                        putVar("jh.url", input)
                        refreshPage(true)
                        return "hiker://empty"
                    }, {
                        cate_temp: cate_temp,
                        index: index,
                        key: key,
                        page: page,
                    }),
                    col_type: 'scroll_button',
                })
            })
            d.push({
                col_type: "blank_block"
            });
        }
    })
}
