var d = [];
let true_url = getVar("huangihe.url", MY_URL)
const page = MY_URL.match(/page\/(.*?)\.html/)[1]
  true_url = true_url.replace('.html', '/page/'+page+'.html').replace('type', 'show')
const html = fetch(true_url)
const empty = "hiker://empty"

const categories = parseDomForArray(html, '.head_menu_a&&ul').concat(parseDomForArray(html, '.row&&.screen_list:not(:contains(语言)):not(:contains(字母))'));

let init_cate = []

for (let i = 0; i < 20; i++) {
    init_cate.push("0")
}

const fold = getVar("huangihe.fold", "0")
const cate_temp_json = getVar("huangihe.category", JSON.stringify(init_cate))
const cate_temp = JSON.parse(cate_temp_json)


if (parseInt(page) === 1) {
    d.push({
        title: fold === '1' ? '““””<b><span style="color: #FF0000">∨</span></b>' : '““””<b><span style="color: #1aad19">∧</span></b>',
        url: $(empty).lazyRule((fold) => {
            putVar("huangihe.fold", fold === '1' ? '0' : '1');
            refreshPage(false);
            return "hiker://empty"
        }, fold),
        col_type: 'scroll_button',
    })


    categories.forEach((category, index) => {
        let sub_categories = parseDomForArray(category, 'body&&a:not(:contains(首页)):not(:contains(资讯))');
        if (index === 0) {
            sub_categories.forEach((item, key) => {
                let title = parseDomForHtml(item, 'a&&Text')
                d.push({
                    title: key.toString() === cate_temp[index] ? '““””<b><span style="color: #098AC1">' + title + '</span></b>' : title,
                    url: $(parseDom(item, 'a&&href')).lazyRule((params) => {
                        let new_cate = []
                        params.cate_temp.forEach((cate, index) => {
                            new_cate.push(index === 0 ? params.key.toString() : "0")
                        })
                        putVar("huangihe.category", JSON.stringify(new_cate))
                        putVar("huangihe.url", input)
                        refreshPage(true)
                        return "hiker://empty"
                    }, {
                        cate_temp: cate_temp,
