function init(){
    clearVar('appDp.error'); //初始预处理清空变量
    const {color, small, api, getFile} = $.require("hiker://page/utiliy?rule=道长仓库Pro");
    let dp_js_name = 'appmovie_dp.js'; //初始化脚本名称
    let app_base_path='hiker://files/rules/dzHouse/dzMovie/';//初始化app影视断插版的基础文件目录
    let app_path_web = '/hikerule/rulelist.json?id=1907';
    let plugin_path = app_base_path+dp_js_name;
    let data_url="/hikerule/zyw_data/"; // 数据接口
    let data_ver="/hikerule/zyw_data_ver/"; // 数据版本接口
    putVar('appDp.bs',app_base_path);
    putVar('appDp.js',plugin_path);
    if(!api||!api.host||!/^http/.test(api.host)){
        putVar('appDp.error',$.toString(()=>{
            let d = [];
            let msg = '点我做什么?自己去找途径安装最新的仓库,这里不提供仓库的导入\n不能用就把规则删了吧';
            d.push({
                title:'获取插件路径失败',
                desc:'道长仓库Pro特供版规则,禁止无仓库用户使用',
                col_type:'text_1',
                url:$(msg).confirm()
            });
            setResult(d);
        }));
        return
    }
    putVar('appDp.js_web',api.host+app_path_web);
    putVar('appDp.data_url',api.host+data_url);
    putVar('appDp.data_ver',api.host+data_ver);

    if(!fetch(plugin_path)){
        let html = fetch(api.host+app_path_web,{timeout:5000});
        if(!/setResult/.test(html)){
            putVar('appDp.error',$.toString(()=>{
                let d = [];
                d.push({
                    title:'获取插件路径失败',
                    desc:'网络问题与仓库通讯出现问题,请尝试刷新试试',
                    col_type:'text_1',
                    url:$().lazyRule(()=>{
                        refreshPage(true);
                        return 'toast://已刷新'
                    })
                });
                setResult(d);
            }));
        }else{
            writeFile(plugin_path,html);

            eval(fetch(plugin_path));
            apppre();
        }
    }else {
        eval(fetch(plugin_path));
        apppre();
    }
}
init();

