[{"col_type":"text_1","name":"Url处理器","path":"url-processor","rule":"function UrlProcessor() {
    this.params = {}
    this.regexMap = [
        {
            reg: /videot\/\d+/,
            exec: () => {
                return this.params.url
                    .replace(/-\d+.html/, '-' + this.params.page + '.html')
                    .replace(/(videot\/\d+).html/, '$1-' + this.params.page + '.html')
            }
        },
        {
            reg: 'vodtype\\/\\d+',
            exec: () => {
                return this.params.url
                    .replace(/-\d+.html/, '-' + this.params.page + '.html')
                    .replace(/(vodtype\/\d+).html/, '$1-' + this.params.page + '.html')
            }
        },
        {
            reg: '\\/?[\\w\\d]+-.*?-.*?-.*?-.*?-.*?-.*?-.*?-\\d*---\\d*.html',
            exec: () => {
                return this.params.url
                    .replace(/(---.html)/, this.params.page + '$1')
                    .replace(/(---\d+.html)/, this.params.page + '$1'); // 拼接页码
            }
        },
    ]
}

Object.assign(UrlProcessor.prototype, {
    constructor: UrlProcessor,
    checkParams() {
        if (!this.params.url) {
            throw new Error("请先调用 .baseUrl(url) 传入链接进行初始化!")
        }
        if (!this.params.page) {
            throw new Error("请先调用 .page(page) 传入页码进行初始化!")
        }
    },
    baseUrl(url){
        this.params.url = url
        return this
    },
    page(page){
        this.params.page = page
        return this
    },
    addExtra(regExp) {
        if (regExp.constructor === Array) {
            this.regexMap = this.regexMap.concat(regExp)
        } else {
            this.regexMap.push(regExp)
        }
        return this;
    },
    exec() {
        this.checkParams()
        let true_url = ''
        for (let i = 0; i < this.regexMap.length; i++) {
            let regObj = this.regexMap[i];
            let urlExp = regObj.reg;
            if (typeof urlExp === 'string') {
                urlExp = new RegExp(urlExp)
            }
            if (urlExp.test(this.params.url)) {
                true_url = regObj.exec();
                break;
            }
        }
        if (true_url) {
            return true_url;
        } else {
            return this.params.url
        }
    },
    获取处理结果() {
        return this.exec()
    },
    链接(url) {
        return this.baseUrl(url)
    },
    页码(page) {
        return this.page(page)
    },
    插入新处理规则(regExp) {
        return this.addExtra(regExp)
    }
})
$.exports=new UrlProcessor()
$.exports
"}]

