   var urls = _getUrls()
   for (var i in urls) {
       if (!urls[i].includes("v=") && !urls[i].includes("url=") && urls[i].match(/\.mp4|\.m3u8/)) {
          fy_bridge_app.log(urls[i])
          return urls[i]
       }
   }
}))
