find_rule:
js:
var d = [];
if (getVar(MY_RULE.group, "0") == 0) {
    require('http://hiker.nokia.press/hikerule/rulelist.json?id=4091');
    顺搜(d)
}

const Color = "#19B89D";

let true_url = getVar(MY_RULE.url, MY_URL)
//log(true_url)
const page = MY_PAGE
//log(page)
if (getVar(MY_RULE.url)) {
    true_url = true_url.replace(/---(\d*)\.html/, +page + '---$1.html');
    log(true_url)
}
var html = fetch(true_url, {
    headers: {
        'User-Agent': 'Mozilla/5.0 (Windows NT 10.0)'
    }
})
const empty = "hiker://empty"

const categories = parseDomForArray(html, '#main&&.scroll-content')

let init_cate = []

for (let i = 0; i < 20; i++) {
    init_cate.push("0")
}

const fold = getVar(MY_RULE.group, "0")
const cate_temp_json = getVar(MY_RULE.title, JSON.stringify(init_cate))
const cate_temp = JSON.parse(cate_temp_json)

if (parseInt(page) === 1) {
    d.push({
        title: fold === '1' ? '““””<b><span style="color: #FF0000">∨</span></b>' : '““””<b><span style="color:' + Color + '">∧</span></b>',
        url: $(empty).lazyRule((fold) => {
            putVar(MY_RULE.group, fold === '1' ? '0' : '1');
            refreshPage(false);
            return "hiker://empty"
        }, fold),
        col_type: 'scroll_button',
    })


    categories.forEach((category, index) => {
        let sub_categories = parseDomForArray(category, 'body&&a');
        if (index === 0) {
            sub_categories.forEach((item, key) => {
                let title = parseDomForHtml(item, 'a&&Text').replace('片库', '')
                d.push({
                    title: key.toString() === cate_temp[index] ? '““””<b><span style="color:' + Color + '">' + title + '</span></b>' : title,
                    url: $(parseDom(item, 'a&&href')).lazyRule((params) => {
                        let new_cate = []
                        params.cate_temp.forEach((cate, index) => {
                            new_cate.push(index === 0 ? params.key.toString() : "0")
                        })
                        putVar(MY_RULE.title, JSON.stringify(new_cate))
                        putVar(MY_RULE.url, input)
                        refreshPage(true)
                        return "hiker://empty"
                    }, {
                        cate_temp: cate_temp,
                        key: key,
                        page: page,
                    }),
                    col_type: 'scroll_button',
                })
            })
            d.push({
                col_type: "blank_block"
            });
        } else if (fold === '1') {
            sub_categories.forEach((item, key) => {
                let title = parseDomForHtml(item, 'a&&Text')
                d.push({
                    title: key.toString() === cate_temp[index] ? '““””<span style="color:' + Color + '">' + title + '</span>' : title,
                    url: $(parseDom(item, 'a&&href')).lazyRule((params) => {
                        params.cate_temp[params.index] = params.key.toString()

                        putVar(MY_RULE.title, JSON.stringify(params.cate_temp))
                        putVar(MY_RULE.url, input)
                        refreshPage(true)
                        return "hiker://empty"
                    }, {
                        cate_temp: cate_temp,
                        index: index,
                        key: key,
                        page: page,
                    }),
                    col_type: 'scroll_button',
                })
            })
            d.push({
                col_type: "blank_block"
            });
        }
    })
}

//一级页面
var list = pdfa(html, 'body&&.module-item');
for (var j in list) {
    d.push({
        title: pdfh(list[j], 'a&&title'),
